import java.util.*;
public class Task3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int a=sc.nextInt();
		long b=a;
		float c=b;
		System.out.println("Integer: " + a);
		System.out.println("Integer --> Long: " + b);
		System.out.println("Long --> Float: " +c);
		
		sc.close();

	}

}
